//Assigned ESU for SQL Query
//This is a 3 part query - needs to include all VMs that have SQL Server installed as an instance (and to subselect for ESU eligible
//SQL Server versions) and then all Machine extensions which contain the ESU assigned True/False flag
//This query can then be used to determine ESU assignment by SQL Version, Edition, Tag (Application), Location etc
//Main Table - All Arc Virtual Machines
resources
| where type == "microsoft.hybridcompute/machines"
| extend mssqlDiscovered = properties.mssqlDiscovered
//Performance - select only machines with SQL Server detected
| where mssqlDiscovered == 'true'
| extend coreCount = toint(properties.detectedProperties.coreCount)
| extend machineId = tolower(tostring(id))
| extend machineName = name
| join kind=inner (
    //Sub Table - Select distinct SQL Server Version (and select on SQL Server 2012)
    resources
    | where type == 'microsoft.azurearcdata/sqlserverinstances'
    | extend version = tostring(properties.version)
    | extend edition = properties.edition
    | extend containerId = tolower(tostring (properties.containerResourceId))
    //add clause back in for live dashboard
    | where version contains "2012"
    | where edition in ("Enterprise", "Standard")
    | where isnotempty(containerId)
    | extend SQL_instance = name
    //Machine may contain multiple instances - this distinct selects the first (assuming all instances on same machine are same SQL Version)
    | distinct containerId)
on $left.machineId == $right.containerId
| join kind=inner (
    //Sub Table - Identify if SQL ESUs assigned
    resources
    //Machine extensions resource type
    | where type == "microsoft.hybridcompute/machines/extensions"
    //Machine has SQL Server extension
    | where properties.type in ("WindowsAgent.SqlServer","LinuxAgent.SqlServer")
    //extract Machine name from ID
    | extend machineIdHasSQLServerExtensionInstalled = tolower(iff(id contains "/extensions/WindowsAgent.SqlServer" or id contains "/extensions/LinuxAgent.SqlServer", substring(id, 0, indexof(id, "/extensions/")), ""))
    //enableExtendedSecurityUpdates has 3 values - Null (ESU not applicable), True (ESU applicable and enabled), False (ESU applicable and disabled)
    | extend  ESU = iff(notnull(properties.settings.enableExtendedSecurityUpdates), iff(properties.settings.enableExtendedSecurityUpdates == true,"enabled","disabled"), "N/A")
    | project machineIdHasSQLServerExtensionInstalled, Extension_State = properties.provisioningState, License_Type = properties.settings.LicenseType,ESU, Extension_Version = properties.instanceView.typeHandlerVersion
)
on $left.machineId == $right.machineIdHasSQLServerExtensionInstalled
//| distinct Machine_name, ESU
//| summarize count(), sum(coreCount) by ESU
| summarize count() by ESU

//Query - All Arc Registered Windows 2014 Servers (SQL Server 2014 will be End of Life shortly)
resources
| where  type == "microsoft.azurearcdata/sqlserverinstances"
| extend version = tostring(properties['version'])
//ESU Assignment (esu eligibility - OS is ESU capable, License Assignment State - OS does/does not have ESU assigned
| extend ESU = tostring(properties.licenseProfile.esuProfile.licenseAssignmentState)
| extend esuEligibility = tostring(properties.licenseProfile.esuProfile.esuEligibility)
| extend esuCount = iff(ESU == "NotAssigned",0,1)
// Change to SQL Server 2014 for live version
| where ['version'] == "SQL Server 2014"
// No ESUs as yet for SQL Server 2014, however pie chart used for consistency - Query will shift when ESUs are available for 2014
| summarize Servers=count() by ESU

//Windows 2012 Servers with no ESU license by Application
//Applicaton breakdown requires tagging of resources
Resources
| where type == "microsoft.hybridcompute/machines"
| extend application = tostring(tags.Application)
| extend osSku = tostring(properties.osSku)
| extend esuLicense= tostring(properties.licenseProfile.esuProfile.licenseAssignmentState)
//Change to Windows Server 2012 for live version
| where osSku contains "Windows Server 2012"
//Select Servers with no license
| where esuLicense == "NotAssigned"
| summarize ServerCount = count() by application

//Assigned ESU for SQL Query
//This is a 3 part query - needs to include all VMs that have SQL Server installed as an instance (and to subselect for ESU eligible
//SQL Server versions) and then all Machine extensions which contain the ESU assigned True/False flag
//This query can then be used to determine ESU assignment by SQL Version, Edition, Tag (Application), Location etc
//Main Table - All Arc Virtual Machines
resources
| where type == "microsoft.hybridcompute/machines"
| extend mssqlDiscovered = properties.mssqlDiscovered
//Performance - select only machines with SQL Server detected
| where mssqlDiscovered == 'true'
| extend coreCount = toint(properties.detectedProperties.coreCount)
| extend machineId = tolower(tostring(id))
| extend Machine_name = name
| join kind=inner (
    //Sub Table - Select distinct SQL Server Version (and select on SQL Server 2012)
    resources
    | where type == 'microsoft.azurearcdata/sqlserverinstances'
    | extend version = tostring(properties.version)
    | extend edition = properties.edition
    | extend application = tostring(tags.Application)
    | extend containerId = tolower(tostring (properties.containerResourceId))
    //add clause back in for live dashboard
    | where version contains "2012"
    | where edition in ("Enterprise", "Standard")
    | where isnotempty(containerId)
    | extend SQL_instance = name
    //Machine may contain multiple instances - this distinct selects the first (assuming all instances on same machine are same SQL Version)
    | distinct containerId, application)
on $left.machineId == $right.containerId
| join kind=inner (
    //Sub Table - Identify if SQL ESUs assigned
    resources
    //Machine extensions resource type
    | where type == "microsoft.hybridcompute/machines/extensions"
    //Machine has SQL Server extension
    | where properties.type in ("WindowsAgent.SqlServer","LinuxAgent.SqlServer")
    //extract Machine name from ID
    | extend machineIdHasSQLServerExtensionInstalled = tolower(iff(id contains "/extensions/WindowsAgent.SqlServer" or id contains "/extensions/LinuxAgent.SqlServer", substring(id, 0, indexof(id, "/extensions/")), ""))
    //enableExtendedSecurityUpdates has 3 values - Null (ESU not applicable), True (ESU applicable and enabled), False (ESU applicable and disabled)
    | extend  ESU = iff(notnull(properties.settings.enableExtendedSecurityUpdates), iff(properties.settings.enableExtendedSecurityUpdates == true,"enabled","disabled"), "N/A")
    | project machineIdHasSQLServerExtensionInstalled, Extension_State = properties.provisioningState, License_Type = properties.settings.LicenseType,ESU, Extension_Version = properties.instanceView.typeHandlerVersion
)
on $left.machineId == $right.machineIdHasSQLServerExtensionInstalled
//| distinct Machine_name, ESU
//| summarize count(), sum(coreCount) by ESU
| summarize count() by application

//Query - Chart of breakdown of Windows 2012 Servers into Datacenter and Standard
//Filter on osTYpe = Windows 
resources
| where type =~ 'microsoft.hybridcompute/machines'
| where properties.osType == "windows"
//**Change to Windows Server 2012
| extend osSku = tostring(properties.osSku)
| where osSku contains "Windows Server 2012"
//Change back to original filter for go live - dummy filter below uses 2019/2022 to create 2 groups
| extend edition = iff(properties.osSku contains "Datacenter", "Datacenter", "Standard")
| summarize serverCount = count() by edition

