//Standard VM Query - VM alone
//Can be used in other queries, pulls out the most important properties for Arc VMs
//Main Table - All Arc Virtual Machines
resources
//Arc registered VMs (Windows & linux)
| where type == "microsoft.hybridcompute/machines"
//Boolean - SQL installed (true/false)
| extend mssqlDiscovered = properties.mssqlDiscovered
//Machine Characteristics
| extend coreCount = toint(properties.detectedProperties.coreCount)
| extend logicalCoreCount = toint(properties.detectedProperties.logicalcoreCount)
| extend processerNames = tostring(properties.detectedProperties.processorNames)
| extend totalPhysicalMemory =  toint(properties.detectedProperties.totalPhysicalMemoryInGigabytes)
//Machine ID & Name
| extend machineId = tolower(tostring(id))
| extend machineName = name
//Operating System
| extend osSku = tostring(properties.osSku)
| extend osName = tostring(properties.osName)
| extend osVersion = tostring(properties.osVersion)
//Windows or Linux
| extend osType = tostring(properties.osType)
| where osType == "windows"
//OS Edition (Datacenter or Standard) 
| extend osEdition = iff(properties.osSku contains "Datacenter", "Datacenter", "Standard")
//ESU Assignment (esu eligibility - OS is ESU capable, License Assignment State - OS does/does not have ESU assigned
| extend ESU = properties.licenseProfile.esuProfile.licenseAssignmentState
| extend esuEligibility = tostring(properties.licenseProfile.esuProfile.esuEligibility)
//Type of ESU assigned (Standard/Data center)
| extend serverType = tostring(properties.licenseProfile.esuProfile.serverType)
| summarize windowsServerCount = count()

