//Count of all Cores
resources
//Arc registered VMs (Windows & linux)
| where type == "microsoft.hybridcompute/machines"
//Machine Characteristics
| extend coreCount = toint(properties.detectedProperties.coreCount)
| summarize CoreCount = sum(coreCount)

//Provisioned Servers
//It is possible for a Server to be known to Arc but not fully provisioned - this query checks that state
resources
//Machine extensions resource type
| where type == "microsoft.hybridcompute/machines/extensions"
//Search for Approriate Agent
| where properties.['type'] in ("AzureMonitorWindowsAgent", "AzureMonitorLinuxAgent")
| extend provisioningState = properties.provisioningState
| extend status = properties.instanceView.status.message
| where provisioningState == "Succeeded"
| summarize totalServersProvisioned = count()

//Connected Servers
//It is possible for a Server to be known to Arc but not fully connected - this query checks that state
resources
//Machine extensions resource type
| where type == "microsoft.hybridcompute/machines/extensions"
| where properties.['type'] in ("AzureMonitorWindowsAgent", "AzureMonitorLinuxAgent")
| extend provisioningState = properties.provisioningState
| extend status = properties.instanceView.status.message
//Check status message (differs for Linux and Windows)
| where status in ("Extension Message: ExtensionOperation:enable. Status:Success", "Extension Message: Enable succeeded")
| summarize totalServersSucceeded = count()

//Standard VM Query - used to count Windows Servers
//Can be used in other queries, pulls out the most important properties for Arc VMs and related SQL Server instances
//Main Table - All Arc Virtual Machines
resources
//Arc registered VMs (Windows & linux)
| where type == "microsoft.hybridcompute/machines"
//Boolean - SQL installed (true/false)
| extend mssqlDiscovered = properties.mssqlDiscovered
//Machine Characteristics
| extend coreCount = toint(properties.detectedProperties.coreCount)
| extend logicalCoreCount = toint(properties.detectedProperties.logicalcoreCount)
| extend processerNames = tostring(properties.detectedProperties.processorNames)
| extend totalPhysicalMemory =  toint(properties.detectedProperties.totalPhysicalMemoryInGigabytes)
//Machine ID & Name
| extend machineId = tolower(tostring(id))
| extend machineName = name
//Operating System
| extend osSku = tostring(properties.osSku)
| extend osName = tostring(properties.osName)
| extend osVersion = tostring(properties.osVersion)
//Windows or Linux
