//All Servers and all SQL Server Instances
//version 1 - Comprehensive query which returns all VMs and also all SQL instances on
//those VMs if they exist.
//used to diagnose missing Agents, License type etc
//Complex Query which operates across 3 resource types
//
//Resource Types used
//1) resources where type == "microsoft.hybridcompute/machines" - All VMs
//2) resourcecontainers where type == 'microsoft.resources/subscriptions' - Owning subscription for VMs
//3) resources where type == 'microsoft.azurearcdata/sqlserverinstances' - collection of SQL Server instances on VM if they exist (outer join used)
//4) resources where type == "microsoft.hybridcompute/machines/extensions" and properties.type in ("WindowsAgent.SqlServer","LinuxAgent.SqlServer") - Extension holding SQLServer Agent for Server if it exists (again, outer join used)
//
// 1) gives us the underlying VM
// 2) gives us the owning subscription
// 3) Selects all Instances
// 4) gets the license status (paid etc) & ESU Status
//
// 1) VMs with SQL
resources 
//Arc registered VMs (Windows & linux)
| where type == "microsoft.hybridcompute/machines"
| extend mssqlDiscovered = properties.mssqlDiscovered
//Machine Characteristics
| extend coreCount = toint(properties.detectedProperties.coreCount)
| extend logicalCoreCount = toint(properties.detectedProperties.logicalcoreCount)
| extend processerNames = tostring(properties.detectedProperties.processorNames)
| extend totalPhysicalMemory =  toint(properties.detectedProperties.totalPhysicalMemoryInGigabytes)
//Machine ID & Name
| extend machineId = tolower(tostring(id))
| extend machineName = name
//Operating System
| extend osSku = tostring(properties.osSku)
| extend osName = tostring(properties.osName)
| extend osVersion = tostring(properties.osVersion)
//Windows or Linux
| extend osType = tostring(properties.osType)
//OS Edition (Datacenter or Standard) 
| extend osEdition = iff(osType == "linux", "N/A",iff(properties.osSku contains "Datacenter", "Datacenter", "Standard"))
//ESU Assignment (esu eligibility - OS is ESU capable, License Assignment State - OS does/does not have ESU assigned
| extend osESU = properties.licenseProfile.esuProfile.licenseAssignmentState
| extend osEsuEligibility = tostring(properties.licenseProfile.esuProfile.esuEligibility)
//Type of ESU assigned (Standard/Data center)
| extend osESUServerType = tostring(properties.licenseProfile.esuProfile.serverType)
//
// 2) Subscription Name
| join kind=inner (
    resourcecontainers
    | where type == 'microsoft.resources/subscriptions'
    | project subscriptionId, subscriptionName = name)
    on subscriptionId
//
// 3) Does the VM host at least 1 instance of SQL Server Standard or Enterprise
| join kind = leftouter  (
    resources
    | where type == 'microsoft.azurearcdata/sqlserverinstances'
    //SQL Server Version & Edition
    | extend sqlVersion = tostring(properties.version)
    | extend sqlEdition = tostring(properties.edition)
    | extend sqlInstance = name
    | extend provisioningState = tostring(properties.provisioningState)
    | extend status = tostring(properties.status)
    | extend sqlID = ['id']
    //needed for join - container is "owning" VM hosting SQL
    | extend containerId = tolower(tostring (properties.containerResourceId))
    | where isnotempty(containerId)
    | extend sqlSku = sku
    | extend sqlLocation = location
    | extend sqlTags = ['tags']
    | extend sqlProperties = properties
    | project-away subscriptionId, resourceGroup, ['type'], tenantId, name, ['id'], kind, location, ['tags'], properties
)
on $left.machineId == $right.containerId
//
//4) Join for SQL Server Agent to retrieve Server Level License Type
| join kind=leftouter  (
    resources
    //Machine extensions resource type
    | where type == "microsoft.hybridcompute/machines/extensions"
    //Machine has SQL Server extension
    | where properties.type in ("WindowsAgent.SqlServer","LinuxAgent.SqlServer")
    | extend sqlAgentInstalled = "Yes"
    | extend licenseType = tostring(properties.settings.LicenseType)
    | extend agentName = name
    //extract Machine name from ID
    | extend machineIdHasSQLServerExtensionInstalled = tolower(iff(id contains "/extensions/WindowsAgent.SqlServer" or id contains "/extensions/LinuxAgent.SqlServer", substring(id, 0, indexof(id, "/extensions/")), ""))
    | extend sqlEnableExtendedSecurityUpdates = properties.settings.enableExtendedSecurityUpdates
    | extend  sqlESU = iff(notnull(sqlEnableExtendedSecurityUpdates), iff(sqlEnableExtendedSecurityUpdates == true,"enabled","disabled"), "N/A")
    | project-away name, resourceGroup, subscriptionId
    )
on $left.machineId == $right.machineIdHasSQLServerExtensionInstalled
| join kind=leftouter  (
	//Provisioned Servers
	//It is possible for a Server to be known to Arc but not fully provisioned - this query checks that state
	resources
	//Machine extensions resource type
	| where type == "microsoft.hybridcompute/machines/extensions"
	//Search for Approriate Agent
	| where properties.['type'] in ("AzureMonitorWindowsAgent", "AzureMonitorLinuxAgent")
	//extract Machine name from ID
	| extend machineIdHasAgentExtensionInstalled = tolower(iff(id contains "/extensions/AzureMonitorWindowsAgent" or id contains "/extensions/AzureMonitorLinuxAgent", substring(id, 0, indexof(id, "/extensions/")), ""))
	| extend provisioningState = properties.provisioningState
	| extend status = properties.instanceView.status.message
	)
on $left.machineId == $right.machineIdHasAgentExtensionInstalled
